riot.tag("tab",'<ul> <li each="{ data }" onclick="{ parent.toggle }" class="{ active: parent.currentIndex==index }">{ title }</li> </ul> <div class="tab-content"> { content } </div>',function(t){var n=this,e=n.opts.opts||n.opts;if(n.data=e.data,n.data.length>0)for(n.currentIndex=0,n.content=n.data[0].content,i=0;i<n.data.length;i++)n.data[i].index=i;this.toggle=function(t){n.content=t.item.content,n.currentIndex=t.item.index,n.update()}.bind(this)});